3. Create empty room decision controls:
```typescript
// components/EmptyRoomControls.tsx
export function EmptyRoomControls({ 
  originalImage, 
  onEmptyRoomGenerated, 
  onAlreadyEmpty 
}) {
  const [isGenerating, setIsGenerating] = useState(false);
  const [retryCount, setRetryCount] = useState(0);
  const [error, setError] = useState<string | null>(null);

  const generateEmptyRoom = async () => {
    setIsGenerating(true);
    setError(null);

    try {
      const response = await fetch('/api/empty', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ imageUrl: originalImage })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to generate empty room');
      }

      const result = await response.json();
      onEmptyRoomGenerated(result.emptyRoomUrl);
    } catch (err) {
      setError(err.message);
      // Show retry button if under max retries
      if (retryCount < 2) {
        setRetryCount(prev => prev + 1);
      }
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="empty-room-controls">
      <h3>Empty Room Generation</h3>
      <div className="button-group">
        <Button 
          onClick={generateEmptyRoom} 
          disabled={isGenerating || !originalImage}
          loading={isGenerating}
        >
          Generate Empty Room
        </Button>
        <Button 
          variant="outline" 
          onClick={() => onAlreadyEmpty(originalImage)}
        >
          Room Already Empty
        </Button>
      </div>

      {error && (
        <div className="error-container">
          <p className="error-message">{error}</p>
          {retryCount < 2 && (
            <Button onClick={generateEmptyRoom}>
              Retry ({2 - retryCount} free retries left)
            </Button>
          )}
        </div>
      )}
    </div>
  );
}
```

