}) {
  const [selectedPreset, setSelectedPreset] = useState('modern');
  const [isGenerating, setIsGenerating] = useState(false);

  const generateStaging = async () => {
    setIsGenerating(true);

    try {
      const response = await fetch('/api/stage', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          emptyRoomUrl: emptyRoomImage,
          stylePreset: selectedPreset
        })
      });

      if (!response.ok) throw new Error('Staging failed');

      const result = await response.json();
      onStagingComplete(result.stagedImageUrls);
    } catch (err) {
      // Handle error
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="staging-presets">
      <div className="preset-selector">
