   ```typescript
   // containers/EmptyRoomContainer.tsx
   export function EmptyRoomContainer({ originalUrl, onComplete }) {
     const { results, selectedResult, retryCount, isLoading, error, setResults, selectResult, incrementRetry, setLoading, setError } = useEmptyRoomStore();
     const { credits } = useCreditsStore();

     // Fetch initial results if not already loaded
     useEffect(() => {
       if (results.length === 0 && !isLoading) {
         generateEmptyRoom(originalUrl);
       }
     }, [originalUrl, results, isLoading]);

     // Function to generate empty room
     const generateEmptyRoom = async (imageUrl, isFreeRetry = true) => {
       try {
         setLoading(true);
         setError(null);

         const response = await fetch('/api/empty', {
           method: 'POST',
           headers: { 'Content-Type': 'application/json' },
           body: JSON.stringify({ 
             imageUrl,
             isFreeRetry
           }),
         });

         if (!response.ok) {
           const error = await response.json();
           throw new Error(error.message || 'Failed to generate empty room');
         }

         const data = await response.json();
         setResults(data.results);
         selectResult(data.results[0]);
       } catch (err) {
         setError(err.message);
       } finally {
         setLoading(false);
       }
     };

     // Handle retry
     const handleRetry = (isFreeRetry) => {
       if (!isFreeRetry && credits < 10) {
         setError('Insufficient credits for retry');
         return;
       }

       if (isFreeRetry) {
         incrementRetry();
       }

       generateEmptyRoom(originalUrl, isFreeRetry);
     };

     // Handle confirmation
     const handleConfirm = () => {
       if (selectedResult) {
         onComplete(selectedResult);
       }
     };

     if (isLoading && results.length === 0) {
       return <LoadingSpinner message="Generating empty room..." />;
     }

     if (error && results.length === 0) {
       return <ErrorMessage message={error} onRetry={() => generateEmptyRoom(originalUrl)} />;
     }

     return (
       <EmptyRoomResults
         originalUrl={originalUrl}
         results={results}
         onSelect={selectResult}
         onConfirm={handleConfirm}
         onRetry={handleRetry}
       />
     );
   }
