2. Create a session management hook using Zustand:
```typescript
// hooks/useSessionStore.ts
import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { v4 as uuidv4 } from 'uuid';
import { SessionState, SessionItem } from '../types/session';

export const useSessionStore = create<SessionState>()(
  persist(
    (set, get) => ({
      sessions: {},
      currentSession: undefined,

      createSession: (originalImageUrl: string) => {
        const id = uuidv4();
        set((state) => ({
          currentSession: id,
          sessions: {
            ...state.sessions,
            [id]: {
              id,
              timestamp: Date.now(),
              originalImageUrl,
              status: 'uploading'
            }
          }
        }));
        return id;
      },

      updateSession: (id: string, updates: Partial<SessionItem>) => {
        set((state) => ({
          sessions: {
            ...state.sessions,
            [id]: {
              ...state.sessions[id],
              ...updates
            }
          }
        }));
      },

      getSession: (id: string) => {
        return get().sessions[id];
      },

      getSessions: () => {
        return Object.values(get().sessions).sort((a, b) => b.timestamp - a.timestamp);
      },

      setCurrentSession: (id: string) => {
        set({ currentSession: id });
      }
    }),
    {
      name: 'virtual-staging-sessions',
      partialize: (state) => ({ sessions: state.sessions }),
    }
  )
);
