  if (!user) return res.status(401).json({ error: 'Unauthorized' });

  const { imageUrl, prompt, model } = req.body;
  const startTime = performance.now();

  try {
    // Call Replicate API with the provided image and prompt
    const prediction = await replicate.run(
      model || "stability-ai/sdxl:39ed52f2a78e934b3ba6e2a89f5b1c712de7dfea535525255b1aa35c5565e08b",
      { input: { image: imageUrl, prompt: prompt } }
    );

    const endTime = performance.now();
    const processingTime = endTime - startTime;

    // Store test results in database
    await db.insert(apiTests).values({
      userId: user.id,
      provider: 'replicate',
      model: model || "stability-ai/sdxl",
      inputImageUrl: imageUrl,
      prompt: prompt,
      outputImageUrl: prediction.output,
      processingTime,
      status: 'success',
      timestamp: new Date()
    });

    return res.status(200).json({
      success: true,
      result: prediction.output,
      processingTime,
      provider: 'replicate'
    });
  } catch (error) {
    const endTime = performance.now();
    const processingTime = endTime - startTime;

    // Store failed test in database
    await db.insert(apiTests).values({
      userId: user.id,
      provider: 'replicate',
      model: model || "stability-ai/sdxl",
      inputImageUrl: imageUrl,
      prompt: prompt,
      processingTime,
      status: 'error',
      errorMessage: error.message,
      timestamp: new Date()
    });

    return res.status(500).json({
      success: false,
      error: error.message,
      processingTime,
      provider: 'replicate'
    });
  }
}
