// components/ApiComparisonDashboard.tsx
import { useState, useEffect } from 'react';
import { Button, Card, Input, Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui';
import { ImageUpload } from '@/components/ImageUpload';
import { ComparisonResults } from '@/components/ComparisonResults';

export function ApiComparisonDashboard() {
  const [imageUrl, setImageUrl] = useState('');
  const [prompt, setPrompt] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [falResults, setFalResults] = useState(null);
  const [replicateResults, setReplicateResults] = useState(null);
  const [comparisonData, setComparisonData] = useState(null);

  const runComparison = async () => {
    if (!imageUrl || !prompt) return;

    setIsLoading(true);
    setFalResults(null);
    setReplicateResults(null);
    setComparisonData(null);

    try {
      // Run tests in parallel
      const [falResponse, replicateResponse] = await Promise.all([
        fetch('/api/test-fal', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ imageUrl, prompt })
        }),
        fetch('/api/test-replicate', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ imageUrl, prompt })
        })
      ]);

      const falData = await falResponse.json();
      const replicateData = await replicateResponse.json();

      setFalResults(falData);
      setReplicateResults(replicateData);

      // Generate comparison metrics
      setComparisonData({
        processingTimeDiff: Math.abs(falData.processingTime - replicateData.processingTime),
        fasterProvider: falData.processingTime < replicateData.processingTime ? 'FAL.AI' : 'Replicate',
        speedupPercentage: Math.round(
          (Math.max(falData.processingTime, replicateData.processingTime) / 
           Math.min(falData.processingTime, replicateData.processingTime) - 1) * 100
        ),
        bothSucceeded: falData.success && replicateData.success
      });
    } catch (error) {
      console.error('Comparison failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">API Comparison Dashboard</h1>

      <Card className="p-4 mb-6">
        <h2 className="text-lg font-semibold mb-2">Test Configuration</h2>
        <div className="space-y-4">
          <ImageUpload 
            onImageSelected={setImageUrl} 
            maxSizeMB={5} 
            className="w-full h-40"
          />

          <Input
            placeholder="Enter prompt for image generation"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            className="w-full"
          />

          <Button 
            onClick={runComparison} 
            disabled={isLoading || !imageUrl || !prompt}
            className="w-full"
          >
            {isLoading ? 'Running Tests...' : 'Run API Comparison'}
          </Button>
        </div>
      </Card>

      {(falResults || replicateResults) && (
        <ComparisonResults
          falResults={falResults}
          replicateResults={replicateResults}
          comparisonData={comparisonData}
        />
      )}
    </div>
  );
}
